package basicprograms;

public class inverttriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=9,i;
		 while(num > 0)
	        {
	            for(i=1; i<=num; i++)
	            {
	                System.out.print(" "+num+" ");
	            }
	            System.out.print("\n");
	            num--;
	        }
	    
	}

}
